import React, { Component } from 'react';
import 'whatwg-fetch';

export default class Cases extends Component {
    render() {
        return (
            <div className="container">
                <main id="case-count">
                    <WorldStats />
                </main>
            </div>
        );
    }
}

class WorldStats extends Component {
    constructor(props) {
        super(props);
        this.state = {
            confirmed: 0,
            recovered: 0,
            deaths: 0
        }
    }

    componentDidMount() {
        fetch('https://wuhan-coronavirus-api.laeyoung.endpoint.ainize.ai/jhu-edu/brief')
            .then((response) => response.json )
            .then ((data) => {
                console.log(data.confirmed);
                this.setState({ confirmed: data.confirmed, recovered: data.recovered, deaths: data.deaths })
            })
    }

    render() {
        let confirmed = this.state.confirmed;
        let deaths = this.state.deaths;
        let recoveries = this.state.recoveries;
        return (            
            <section>
                <h1>Global Coronavirus Statistics</h1>
                <h2>Last Updated: </h2>
                <h3>Confirmed Cases: {confirmed}</h3>
                <h3>Confirmed Deaths: {deaths}</h3>
                <h3>Confirmed Recoveries: {recoveries}</h3>
            </section>
        );
    }
}
