import React, { Component } from 'react';
import 'whatwg-fetch';

export default class Cases extends Component {
    render() {
        return (
            <div className="container">
                <main id="case-count">
                    <WorldStats />
                </main>
            </div>
        );
    }
}

class WorldStats extends Component {
    constructor(props) {
        super(props);
        this.state = {
            confirmed: 0,
            deaths: 0,
            recoveries: 0
        }
    }

    componentDidMount() {
        fetch('https://wuhan-coronavirus-api.laeyoung.endpoint.ainize.ai/jhu-edu/brief')
            .then((response) => response.json() )
            .then((data) => {
                this.setState({ confirmed: data.confirmed, deaths: data.confirmed, recoveries: data.recovered })
            })
    }

    render() {
        return (            
            <section>
                <h1>Global Coronavirus Statistics</h1>
                <h2>Last Updated: </h2>
                <h3>Confirmed Cases: {this.state.confirmed}</h3>
                <h3>Confirmed Deaths: {this.state.deaths}</h3>
                <h3>Confirmed Recoveries: {this.state.recoveries}</h3>
            </section>
        );
    }
}
